import React, { Component } from 'react';
import './App.css';
import practiceCode from './practiceCode/code'
import home from './home/home'
import book from './book/book'
import { Route, NavLink,HashRouter} from 'react-router-dom';



class App extends Component{
  state = {
    items: []
  }

  getItems(){
    fetch('http://localhost:5000/users#/', {mode: 'no-cors'})
      .then(response => response.json())
      .then(items => this.setState({items}))
      .catch(err => console.log(err))
  }

  addItemToState = (item) => {
    this.setState(prevState => ({
      items: [...prevState.items, item]
    }))
  }

  updateState = (item) => {
    const itemIndex = this.state.items.findIndex(data => data.id === item.id)
    const newArray = [
    // destructure all items from beginning to the indexed item
      ...this.state.items.slice(0, itemIndex),
    // add the updated item to the array
      item,
    // add the rest of the items to the array from the index after the replaced item
      ...this.state.items.slice(itemIndex + 1)
    ]
    this.setState({ items: newArray })
  }

  deleteItemFromState = (id) => {
    const updatedItems = this.state.items.filter(item => item.id !== id)
    this.setState({ items: updatedItems })
  }

  componentDidMount(){
    this.getItems()
  }

  render(){
    console.log(this.state.items)
    return (
      <div className='background' >
        <HashRouter>
          <ul className='header'>
            <li><NavLink to="/">About  </NavLink></li>
            <li><NavLink to="/code">Practice Code</NavLink></li>
            <li><NavLink to="/book">Read Book</NavLink></li>
          </ul>
          <div className='content'>
            <Route exact path='/' component={home}></Route>
            <Route path='/code' component={practiceCode}></Route>
            <Route path='/book' component={book}></Route>
          </div>
        </HashRouter>
      </div>

    );
  }
  }

export default App;
